mapper.select:
SELECT count( bpoh.pk_product_order ) FROM bill_product_order_h bpoh WHERE bpoh.dr = 0
AND bpoh.bill_state = #{billProductOrderHPo.billState}
AND bpoh.taxinclud = #{billProductOrderHPo.taxinclud}

SELECT bpoh.pk_product_order as pkProductOrder, bpoh.contract_no as contractNo, bpoh.pk_customer as pkCustomer, bcuone.name as customerName, bcuone.tel as customerTel, bcuone.corpaddress, bpoh.pk_psndoc as pkPsndoc, bpoh.pk_takeover as pkTakeover, bpoh.material_source as materialSource, bpoh.material_source as materialSourceShow, bpoh.taxinclud, bpoh.urgent, bpoh.urgent as urgentShow, bpoh.transport_type as transportType, bpoh.transport_type as transportTypeShow, bpoh.payment_type as paymentType, bpoh.payment_type as paymentTypeShow, bpoh.creator, bcu.name as creatorShow, bpoh.creationtime, bpoh.modifier, bcut.name as modifierShow, bpoh.modifiedtime, bpoh.dr, bpoh.ts, bpoh.bill_state as billState FROM bill_product_order_h bpoh LEFT JOIN bd_customer bcuone ON bcuone.pk_customer = bpoh.pk_customer AND bcuone.dr = 0 LEFT JOIN bd_customer bcu ON bpoh.pk_customer = bcu.pk_customer AND bcu.dr = 0 LEFT JOIN bd_customer bcut ON bpoh.modifier = bcut.pk_customer AND bcut.dr = 0 WHERE bpoh.dr = 0
AND bpoh.bill_state = #{billProductOrderHPo.billState}
AND bpoh.taxinclud = #{billProductOrderHPo.taxinclud}
AND bpoh.pk_product_order = #{billProductOrderHPo.pkProductOrder}

mapper.update:
UPDATE bill_product_order_h SET dr = 1
WHERE pk_product_order in

    #{pk_product_order}

UPDATE bill_product_order_b SET dr = 1
WHERE pk_product_order in

    #{pk_product_order}
